        private void BtnBuscarArchivoFirmas_Click(object sender, EventArgs e)
        {
            string dirArchivo = null;
            OpenFileDialog miOpenF = new OpenFileDialog();
            miOpenF.Filter = "Excel files (*.xlsx)|*.xls" + "|All files (*.*)|*.*";
            miOpenF.Multiselect = false;
            miOpenF.Title = "Seleccione la carpeta donde se encuentra el archivo";

            if (miOpenF.ShowDialog() == DialogResult.OK)
            {
                dirArchivo = miOpenF.FileName;
            }

            Microsoft.Office.Interop.Excel.Application excelApp = new Microsoft.Office.Interop.Excel.Application();

            if (excelApp != null)
            {
                Microsoft.Office.Interop.Excel.Workbook excelWorkbook = excelApp.Workbooks.Open(dirArchivo, 0, true, 5, "", "", true, Microsoft.Office.Interop.Excel.XlPlatform.xlWindows, "\t", false, false, 0, true, 1, 0);
                Microsoft.Office.Interop.Excel.Worksheet excelWorksheet = (Microsoft.Office.Interop.Excel.Worksheet)excelWorkbook.Sheets[1];

                System.Data.DataTable dataTable = new System.Data.DataTable();

                Microsoft.Office.Interop.Excel.Range excelRange = excelWorksheet.UsedRange;
                int rowCount = excelRange.Rows.Count;
                int colCount = excelRange.Columns.Count;

                DataRow row = null;

                for (int i = 1; i <= rowCount; i++)
                {
                    if (i != 1)
                        row = dataTable.NewRow();
                    for (int j = 1; j <= colCount; j++)
                    {
                        if (i == 1)
                            dataTable.Columns.Add(excelRange.Cells[1, j].value);
                        else
                        {
                            if (!String.IsNullOrEmpty(excelRange.Cells[i, 1].value2.ToString()))
                            {
                                row[j - 1] = excelRange.Cells[i, j].value;
                            }
                        }
                    }
                    if (row != null && !String.IsNullOrEmpty(row[1].ToString()))
                        dataTable.Rows.Add(row);
                }
                dataFirma.Columns.Clear();
                dataFirma.DataSource = dataTable;

                excelWorkbook.Close();
                excelApp.Quit();
            }
        }
